INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (52605,10801,'A','<p>exerc&iacute;cio de tutela sobre atividades jur&iacute;dicas e&nbsp;pol&iacute;ticas.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A divis&atilde;o dos poderes n&atilde;o presume tutela sobre atividades jur&iacute;dicas ou pol&iacute;ticas, mas exatamente a autonomia dos poderes Legislativo, Executivo e Judici&aacute;rio. Assim, nenhuma dessas partes controla a totalidade do poder, mas a divis&atilde;o garante a harmonia entre eles.</p>\r\n',0);
