INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (52607,10801,'C','<p>concentra&ccedil;&atilde;o do poder nas m&atilde;os de elites&nbsp;t&eacute;cnico-cientif&iacute;cas.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Diferentemente do que a alternativa indica, a teoria dos tr&ecirc;s poderes n&atilde;o presume que as elites t&eacute;cnico-cient&iacute;ficas dominem exerc&iacute;cio do poder no Estado, tampouco que ele se concentre na m&atilde;o de qualquer grupo.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (52608,10801,'D','<p>estabelecimento de limites aos atores p&uacute;blicos e &agrave;s&nbsp;institui&ccedil;&otilde;es do governo.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>Os limites aos quais a alternativa faz refer&ecirc;ncias seriam aqueles estabelecidos, para qualquer um dos poderes &mdash;&nbsp;Legislativo, Executivo e Judici&aacute;rio &mdash; pelos outros dois. Na medida em que atuam de forma independente, um n&atilde;o deve interferir nos assuntos que condizem ao outro, e assim os limites surgem da n&atilde;o concentra&ccedil;&atilde;o de poder.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (52609,10801,'E','<p>reuni&atilde;o das fun&ccedil;&otilde;es de legislar, julgar e executar nas&nbsp;m&atilde;os de um governante eleito.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A divis&atilde;o e independ&ecirc;ncia dos poderes presume que n&atilde;o haja &ldquo;reuni&atilde;o das fun&ccedil;&otilde;es de legislar, julgar e executar nas m&atilde;os&rdquo; de qualquer governante, independentemente se ela for eleito ou n&atilde;o.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (54077,11094,'A','<p>mercantiliza&ccedil;&atilde;o das cren&ccedil;as religiosas.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O texto aponta para uma mudan&ccedil;a de pensamento acerca quest&atilde;o da morte, que se relaciona diretamente com mudan&ccedil;as culturais que acontecem no tempo e que fazem com que certas ideias e narrativas sociais sejam transformadas, n&atilde;o se relacionando diretamente &agrave; quest&atilde;o econ&ocirc;mica de mercantiliza&ccedil;&atilde;o.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (54078,11094,'B','<p>transforma&ccedil;&atilde;o das representa&ccedil;&otilde;es sociais.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>O pensamento sobre a quest&atilde;o do que acontecer&aacute; ap&oacute;s a morte est&aacute; diretamente ligado ao modo como a cultura se modificou com o passar do tempo, n&atilde;o apenas em quest&otilde;es religiosas, mas, por exemplo, em rela&ccedil;&atilde;o ao desenvolvimento da ci&ecirc;ncia moderna.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (54079,11094,'C','<p>dissemina&ccedil;&atilde;o do ate&iacute;smo nos pa&iacute;ses de maioria crist&atilde;.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Mesmo que o cristianismo n&atilde;o tenha mais o espa&ccedil;o que antes tinha, n&atilde;o &eacute; s&oacute; o ate&iacute;smo o respons&aacute;vel pela mudan&ccedil;a do pensamento e das d&uacute;vidas em rela&ccedil;&atilde;o &agrave; morte. A ci&ecirc;ncia moderna e as outras express&otilde;es de religiosidade tamb&eacute;m podem estar relacionadas a isso.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (54080,11094,'D','<p>diminui&ccedil;&atilde;o da dist&acirc;ncia entre saber cient&iacute;fico e eclesi&aacute;stico.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Com o advento da ci&ecirc;ncia moderna, separou-se bem mais o que pertenceria ao campo religioso e o que seria cient&iacute;fico, objetivo. Assim, a quest&atilde;o de f&eacute; na religi&atilde;o ou na ci&ecirc;ncia situa-se em campos bem separados.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (54081,11094,'E','<p>amadurecimento da consci&ecirc;ncia ligada &agrave; civiliza&ccedil;&atilde;o moderna.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>N&atilde;o se pode classificar a mudan&ccedil;a cultural ocorrida com o passar dos s&eacute;culos como amadurecimento, pois n&atilde;o se pode atribuir uma valora&ccedil;&atilde;o &agrave;s mudan&ccedil;as sociais em compara&ccedil;&atilde;o a um tempo passado.</p>\r\n',0);
