INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (59313,12119,'A','<p>submiss&atilde;o de g&ecirc;nero, apoiada pela concep&ccedil;&atilde;o patriarcal de fam&iacute;lia.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>&Eacute; comum nos dois textos a imagem de que as mulheres da sociedade brasileira no s&eacute;culo XIX eram submetidas a um modelo de feminilidade, que tamb&eacute;m envolvia que a mulher estivesse presa ao lar, ao dom&eacute;stico. Ressalta-se, tamb&eacute;m, que esse modelo era defendido e reproduzido pela sociedade e pela fam&iacute;lia patriarcal. Assim, o Texto I apresenta uma vis&atilde;o idealizada do universo feminino, ao passo que o Texto II mostra uma mulher privada da vida social.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (59314,12119,'B','<p>acesso aos produtos de beleza, decorr&ecirc;ncia da abertura dos portos.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Nesse contexto, o uso dos produtos de beleza n&atilde;o est&aacute; relacionado &agrave; abertura dos portos, mas sim ao padr&atilde;o estabelecido para as mulheres, e as que tinham recursos financeiros para isso procuravam usufruir para estarem adequadas ao padr&atilde;o social patriarcal, que &eacute; o que se pode relacionar analisando ambos os textos.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (59315,12119,'C','<p>amplia&ccedil;&atilde;o do espa&ccedil;o de entretenimento, voltado &agrave;s distintas classes sociais.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Os espa&ccedil;os poss&iacute;veis para as mulheres de classes m&eacute;dias e altas, as representadas nos textos, eram os espa&ccedil;os dom&eacute;sticos e do lar. As atividades que podiam fazer todas se relacionavam &agrave; constru&ccedil;&atilde;o de uma esposa feminina, bem dotada, para agradar ao marido e as exig&ecirc;ncias da sociedade patriarcal.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (59316,12119,'D','<p>prote&ccedil;&atilde;o da honra, mediada pela disputa masculina em rela&ccedil;&atilde;o &agrave;s damas da corte.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A prote&ccedil;&atilde;o da honra n&atilde;o &eacute; o aspecto apresentado na rela&ccedil;&atilde;o dos dois textos, e sim a constru&ccedil;&atilde;o das mulheres subjugadas a uma sociedade patriarcal. A prote&ccedil;&atilde;o de sua honra &eacute; uma consequ&ecirc;ncia disso, e ela estava relegada n&atilde;o s&oacute; &agrave;s mulheres damas da corte.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (59317,12119,'E','<p>valoriza&ccedil;&atilde;o do casamento crist&atilde;o, respaldado pelos interesses vinculados &agrave; heran&ccedil;a.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A valoriza&ccedil;&atilde;o apresentada nos textos &eacute; de uma mulher feminina, a qual fosse relegada aos espa&ccedil;os dom&eacute;sticos e a atividades que a formassem como uma boa esposa e m&atilde;e.</p>\r\n',0);
