INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (53140,10908,'A','<p>solubiliza&ccedil;&atilde;o dos objetos.</p>\r\n','<p>INCORRETA.<br />\r\nO aluno que seleciona essa alternativa acredita que a solubiliza&ccedil;&atilde;o de pol&iacute;meros permite a quebra das liga&ccedil;&otilde;es covalente presentes em sua estrutura.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (53141,10908,'B','<p>combust&atilde;o dos objetos.</p>\r\n','<p>INCORRETA.<br />\r\nO aluno que seleciona essa alternativa relaciona a combust&atilde;o como uma forma de reciclagem de materiais, na qual ocorre a quebra das liga&ccedil;&otilde;es qu&iacute;micas e forma&ccedil;&atilde;o de novas estruturas, mas n&atilde;o compreende que, dessa forma, n&atilde;o &eacute; poss&iacute;vel obter o etilenoglicol.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (53142,10908,'C','<p>tritura&ccedil;&atilde;o dos objetos.</p>\r\n','<p>INCORRETA.<br />\r\nO aluno que seleciona essa alternativa faz uso do senso comum, segundo o qual a tritura&ccedil;&atilde;o &eacute; uma etapa comum durante o processo reciclagem, mas n&atilde;o compreende que triturar &eacute; um processo f&iacute;sico no qual n&atilde;o ocorre quebra das liga&ccedil;&otilde;es qu&iacute;micas do pol&iacute;mero.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (53143,10908,'D','<p>hidr&oacute;lise dos objetos.</p>\r\n','<img src=\"https://img.enem.appprova.com.br/alternative/justifications/images/3429/original/simulado.png\" /><p>CORRETA.<br />\r\nA rea&ccedil;&atilde;o de hidr&oacute;lise do politereftalato de etileno (PET), que apresenta grupo funcional caracter&iacute;stico de &eacute;ster produz um &aacute;cido dicarbox&iacute;lico e um di&aacute;lcool, no caso, o etano-1,2-diol ou etilenoglicol, conforme a rea&ccedil;&atilde;o da figura.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (53144,10908,'E','<p>fus&atilde;o dos objetos.</p>\r\n','<p>INCORRETA.<br />\r\nO aluno que seleciona essa alternativa associa a fus&atilde;o &agrave; quebra de liga&ccedil;&otilde;es covalentes existentes no material, sem compreender que, por ser um processo de mudan&ccedil;a de estado f&iacute;sico, n&atilde;o &eacute; poss&iacute;vel obter o etilenoglicol a partir do PET.</p>\r\n',0);
