INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (52551,10790,'B','<p>as variedades do espanhol t&ecirc;m igual relev&acirc;ncia lingu&iacute;stica e cultural.</p>\r\n','<p>CORRETA<br />\r\nEsta diferencia&ccedil;&atilde;o, presente no primeiro par&aacute;grafo, atesta a valoriza&ccedil;&atilde;o das variedades do espanhol: &ldquo;Unidad y unificaci&oacute;n no son lo mismo: launidad ha existido siempre y com ella la variedad de la lengua, <strong>riqueza suprema</strong> de nuestras culturas nacionales; la unificaci&oacute;n lleva a la <strong>p&eacute;rdida de las diferencias culturales</strong>, que nutren al ser humano y sontan importantes como La diversidad biol&oacute;gica de la Tierra&rdquo;. O trecho final refor&ccedil;a a mesma ideia: &ldquo;El espa&ntilde;ol actual es el conjunto de veintid&oacute;s espa&ntilde;oles nacionales, que tienen sus propias caracter&iacute;sticas; ninguno vale m&aacute;s que otro. La lengua Del siglo XXI es, por eso, una <strong>lengua pluric&eacute;ntrica</strong>.&rdquo;</p>\r\n',1);
