INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (52595,10799,'A','<p>a carta de Pero Vaz de Caminha representa uma das primeiras manifesta&ccedil;&otilde;es art&iacute;sticas dos portugueses em terras brasileiras e preocupa-se apenas com a est&eacute;tica liter&aacute;ria.</p>\r\n','<p>INCORRETA<br />\r\nA carta pertence a uma vertente da literatura Quinhentista chamada de Literatura de Informa&ccedil;&atilde;o. Uma das caracter&iacute;sticas inerentes &agrave; vertente &eacute; a aus&ecirc;ncia de compromisso est&eacute;tico e liter&aacute;rio, visando apenas informar. Caso n&atilde;o perceba a improced&ecirc;ncia da afirma&ccedil;&atilde;o do distrator, o aluno ser&aacute; levado a marc&aacute;-lo.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (52596,10799,'B','<p>a tela de Portinari retrata ind&iacute;genas nus com corpos pintados, cuja grande significa&ccedil;&atilde;o &eacute; a afirma&ccedil;&atilde;o da arte acad&ecirc;mica brasileira e a contesta&ccedil;&atilde;o de uma linguagem moderna.</p>\r\n','<p>INCORRETA<br />\r\nA arte de Portinari em momento algum contesta a linguagem moderna, pelo contr&aacute;rio, ela faz parte da tradi&ccedil;&atilde;o Modernista. O aluno pode marcar esse distrator caso desconhe&ccedil;a essa informa&ccedil;&atilde;o.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (52597,10799,'C','<p>a carta, como testemunho hist&oacute;rico-pol&iacute;tico, mostra o olhar do colonizador sobre a gente da terra, e a pintura destaca, em primeiro plano, a inquieta&ccedil;&atilde;o dos nativos.</p>\r\n','<p>CORRETA<br />\r\nA carta de Pero Vaz de Caminha &eacute; marcada pelos tra&ccedil;os de uma literatura meramente informativa, pois descreve com detalhes os primeiros contatos do colonizador com os colonizados. J&aacute; a pintura faz refer&ecirc;ncia ao comportamento dos &iacute;ndios no primeiro plano da pintura. Eles apareceram na costa assim que identificaram a nau portuguesa, como narrado na carta.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (52598,10799,'D','<p>as duas produ&ccedil;&otilde;es, embora usem linguagens diferentes &mdash; verbal e n&atilde;o verbal &mdash;, cumprem a mesma fun&ccedil;&atilde;o social e art&iacute;stica.</p>\r\n','<p>INCORRETA<br />\r\nA carta &eacute; informativa a pintura &eacute; liter&aacute;ria/art&iacute;stica, portanto, a fun&ccedil;&atilde;o dos textos &eacute; diferente. O aluno pode marcar esse distrator caso confunda fun&ccedil;&atilde;o social e tema.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (52599,10799,'E','<p>a pintura e a carta de Caminha s&atilde;o manifesta&ccedil;&otilde;es de grupos &eacute;tnicos diferentes, produzidas em um mesmo momento hist&oacute;rico, retratando a coloniza&ccedil;&atilde;o.</p>\r\n','<p>INCORRETA<br />\r\nO momento hist&oacute;rico da produ&ccedil;&atilde;o das duas obras &eacute; distinto, separadas por um recorte temporal consider&aacute;vel (Caminha escreveu logo ap&oacute;s o Descobrimento do Brasil, no s&eacute;culo XVI enquanto Portinari pintou a tela em 1965, no s&eacute;culo XX. O aluno s&oacute; marcar&aacute; esse distrator caso n&atilde;o se atente para as datas, presentes nas refer&ecirc;ncias das obras.</p>\r\n',0);
