INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (52695,10819,'A','<p>o discurso ambientalista prop&otilde;e formas radicais de resolver os problemas clim&aacute;ticos.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>N&atilde;o &eacute; objetivo do cartaz fazer uma avalia&ccedil;&atilde;o do discurso ambientalista ou de sua forma de tratar problemas ambientais, mas levar o leitor a avaliar a pr&oacute;pria responsabilidade com rela&ccedil;&atilde;o &agrave; preserva&ccedil;&atilde;o do meio ambiente.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (52696,10819,'B','<p>a preserva&ccedil;&atilde;o da vida na Terra depende de a&ccedil;&otilde;es de dessaliniza&ccedil;&atilde;o da &aacute;gua marinha.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O verbo &ldquo;derreter&rdquo;, no texto do cartaz, n&atilde;o remete ao ambiente marinho, mas ao das geleiras, j&aacute; que o cartaz faz refer&ecirc;ncia ao aquecimento global, que causa degelo e desequil&iacute;brio na natureza, amea&ccedil;ando a vida na Terra.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (52697,10819,'C','<p>a acomoda&ccedil;&atilde;o da topografia terrestre desencadeia o natural degelo das calotas polares.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O degelo das calotas polares, segundo o cartaz, n&atilde;o &eacute; um fen&ocirc;meno natural, mas sim uma consequ&ecirc;ncia do aquecimento global, ocasionado pela a&ccedil;&atilde;o do ser humano de n&atilde;o preservar o meio ambiente.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (52698,10819,'D','<p>o descongelamento das calotas polares diminui a quantidade de &aacute;gua doce pot&aacute;vel do mundo.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O problema do descongelamento das calotas, conforme se depreende do cartaz, n&atilde;o &eacute; amea&ccedil;ar a quantidade de &aacute;gua doce, mas tudo que est&aacute; relacionado &agrave; vida no planeta, pois causa um desequil&iacute;brio na natureza como um todo.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (52699,10819,'E','<p>a agress&atilde;o ao planeta &eacute; dependente da posi&ccedil;&atilde;o assumida pelo homem frente aos problemas ambientais.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>Ao empregar uma imagem do planeta dentro de uma gota, com um verbo associado ao tema meio ambiente (derreter) e conjugado em cada pessoa do discurso, o texto sugere que cada ser humano &eacute; sujeito das pr&oacute;prias a&ccedil;&otilde;es, tendo sua parcela de responsabilidade sobre a preserva&ccedil;&atilde;o ou agress&atilde;o ao planeta, fazendo refer&ecirc;ncia ao aquecimento global. Por fim, ao se deter sobre a segunda pessoa, &ldquo;voc&ecirc;&rdquo;, o texto questiona o leitor sobre sua postura, levando-o a refletir sobre a posi&ccedil;&atilde;o assumida por ele perante a degrada&ccedil;&atilde;o do meio ambiente.</p>\r\n',1);
