INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (53474,10974,'E','<p>4,00.</p>\r\n','<p>CORRETA.</p>\r\n\r\n<p>Ap&oacute;s a remarca&ccedil;&atilde;o de pre&ccedil;os dos produtos da loja, o produto que custava 50 reais passou a custar 80% do pre&ccedil;o original, o que significa que ele passou a custar 50.0,8 = 40 reais. Se tivesse o cart&atilde;o fidelidade, pagaria apenas 90% em rela&ccedil;&atilde;o a este &uacute;ltimo valor,&nbsp;ou seja, pagaria 40.0,9 = 36 reais. Deste modo, o cliente deixou de ganhar um desconto de 40 -&nbsp;36 = 4 reais, pois n&atilde;o possu&iacute;a o cart&atilde;o fidelidade da loja.</p>\r\n',1);
