INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (53701,11020,'B','<p>bom.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>Seja y o n&uacute;mero de parafusos produzidos pelas m&aacute;quinas I e II no m&ecirc;s de setembro, temos que:</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>I) O n&uacute;mero de parafusos defeituosos produzidos pela m&aacute;quina I &eacute;:</p>\r\n\r\n<p>$\\dfrac{25}{1000} \\times \\dfrac{54}{100} \\times y = \\dfrac{1,35}{100} \\times y$</p>\r\n\r\n<p>O n&uacute;mero de parafusos defeituosos produzidos pela m&aacute;quina II &eacute;:</p>\r\n\r\n<p>$\\dfrac{38}{1000} \\times \\dfrac{46}{100} \\times y = \\dfrac{1,748}{100} \\times y$</p>\r\n\r\n<p>Assim, $P= \\dfrac{\\dfrac{1,35}{100} \\times y + \\dfrac{1,748}{100} \\times y}{y} = \\dfrac{3,098}{100}$&nbsp;e, portanto, o desempenho da m&aacute;quina &eacute; bom.</p>\r\n',1);
