INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62249,12709,'A','<p>o envio de jovens &agrave; guerra para promover a expans&atilde;o territorial dos Estados Unidos.</p>\r\n','<p>INCORRETA<br />\r\nEmbora o envio de soldados para a guerra seja um tema identific&aacute;vel na letra da m&uacute;sica, n&atilde;o h&aacute; refer&ecirc;ncia a uma expans&atilde;o territorial, nem a um pa&iacute;s espec&iacute;fico, j&aacute; que a mensagem pode ser entendida como uma cr&iacute;tica ao sistema de recrutamento de soldados de qualquer na&ccedil;&atilde;o que tenha unicamente interesses econ&ocirc;micos com as guerras.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62250,12709,'B','<p>o comportamento dos soldados norte-americanos nas guerras de que participaram.</p>\r\n','<p>INCORRETA<br />\r\nA letra da m&uacute;sica n&atilde;o faz refer&ecirc;ncia ao campo de guerra, de modo que n&atilde;o trata sobre o comportamento dos soldados durante as disputas. O foco da letra &eacute; o comportamento dos &ldquo;mestres da guerra&rdquo;, aqueles que det&ecirc;m o poder de recrutar e enviar soldados para as batalhas, por&eacute;m, escondem-se atr&aacute;s de suas mesas e n&atilde;o sofrem as consequ&ecirc;ncias da guerra.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62251,12709,'C','<p>o sistema que recruta soldados para guerras motivadas por interesses econ&ocirc;micos.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>J&aacute; no t&iacute;tulo, h&aacute; refer&ecirc;ncia ao sistema de recrutamento de soldados, representado pelos &ldquo;mestres da guerra&rdquo;, aqueles que constroem armas e as colocam nas m&atilde;os desses soldados para que estes lutem nas guerras. Os mestres da guerra, por&eacute;m, que det&ecirc;m o poder de decis&atilde;o sobre seus comandados, se escondem atr&aacute;s de suas mesas em suas salas e ficam longe dos perigos da guerra. Portanto, a cr&iacute;tica recai sobre o sistema de recrutamento de soldados para guerras motivadas por interesses econ&ocirc;micos.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62252,12709,'D','<p>o desinteresse do governo pelas fam&iacute;lias dos soldados mortos em campos de batalha.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A letra de m&uacute;sica n&atilde;o faz refer&ecirc;ncia &agrave;s fam&iacute;lias de soldados mortos, mas sim ao recrutamento e envio de soldados que ter&atilde;o de lutar por interesses que n&atilde;o s&atilde;o seus, mas de quem os recruta e envia, isto &eacute;, os &ldquo;mestres da guerra&rdquo;.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62253,12709,'E','<p>as For&ccedil;as Armadas norte-americanas, que enviavam homens despreparados para as guerras.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Ao contr&aacute;rio do que diz a alternativa em quest&atilde;o, a letra de m&uacute;sica relata que os soldados s&atilde;o bem equipados com armas, bombas e avi&otilde;es, por&eacute;m eles lutam por interesses alheios, que n&atilde;o s&atilde;o seus, mas dos &ldquo;mestres da guerra&rdquo; que os recrutam e enviam.</p>\r\n',0);
