INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62264,12712,'A','<p>desalento frente &agrave;s adversidades naturais.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>O autor cita tr&ecirc;s fen&ocirc;menos naturais que dificultam a ocupa&ccedil;&atilde;o humana do espa&ccedil;o em quest&atilde;o: a fertilidade selvagem e incontorn&aacute;vel da terra, que n&atilde;o permite ao homem a prosperidade de suas planta&ccedil;&otilde;es; as estradas abertas sendo sufocadas pela vegeta&ccedil;&atilde;o e essas mesmas estradas sendo inundadas. Portanto, o que est&aacute; essencialmente sendo ressaltada pelo escritor com rela&ccedil;&atilde;o &agrave; realidade paraguaia &eacute; a sensa&ccedil;&atilde;o de tristeza frente &agrave;s dificuldades impostas pela natureza na sobreviv&ecirc;ncia de comunidades humanas.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62265,12712,'B','<p>amplo conhecimento da flora paraguaia.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A natureza &eacute; tema do texto, por&eacute;m, n&atilde;o h&aacute; demonstra&ccedil;&atilde;o de conhecimentos sobre a flora nesse fragmento.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62266,12712,'C','<p>impossibilidade de cultivo da terra.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Menciona-se a impossibilidade de cultivo da terra devido ao fato de outras plantas crescerem junto &agrave;quela semeada pelo homem, o que impossibilita a colheita e a pr&oacute;pria diferencia&ccedil;&atilde;o da planta semeada entre as demais. Entretanto, essa &eacute; apenas uma das adversidades naturais citadas, n&atilde;o configurando em sua ess&ecirc;ncia a percep&ccedil;&atilde;o do autor sobre a realidade paraguaia, mas apenas um ponto dessa percep&ccedil;&atilde;o.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62267,12712,'D','<p>necessidade de se constru&iacute;rem novos caminhos.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>J&aacute; que os caminhos constru&iacute;dos pelo homem s&atilde;o destru&iacute;dos por vegeta&ccedil;&otilde;es e inunda&ccedil;&otilde;es, poder-se-ia apreender erroneamente que h&aacute; necessidade de constru&ccedil;&atilde;o de novos caminhos. Mas isso n&atilde;o representa a percep&ccedil;&atilde;o do autor.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62268,12712,'E','<p>despreparo do agricultor no trato com a terra.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Apesar de haver margem para se pensar que as planta&ccedil;&otilde;es n&atilde;o vingam devido ao despreparo do agricultor, a &ecirc;nfase do texto est&aacute; na incontornabilidade dos fen&ocirc;menos naturais que impossibilitam o cultivo agr&iacute;cola. Ademais, ainda que o agricultor fosse considerado despreparado no texto, isso n&atilde;o seria a total percep&ccedil;&atilde;o do escritor, mas apenas um ponto dela.</p>\r\n',0);
