INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62294,12718,'A','<p>alertar a sociedade sobre os riscos de ser espionada.</p>\r\n','<p>INCORRETA<br />\r\nO texto at&eacute; fala sobre a preocupa&ccedil;&atilde;o da sociedade em rela&ccedil;&atilde;o &agrave; inseguran&ccedil;a no mundo digital (&ldquo;A sociedade caiu em si e viu que &eacute; uma coisa que nos afeta&rdquo;) e isso pode levar &agrave; marca&ccedil;&atilde;o dessa resposta como correta. Entretanto, apesar de citar a sociedade, todos os argumentos e opini&otilde;es de autoridade apresentados giram n&atilde;o objetivam alertar a sociedade, mas expor a necessidade de se programar medidas de seguran&ccedil;a da informa&ccedil;&atilde;o.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62295,12718,'B','<p>promover a ind&uacute;stria de seguran&ccedil;a da informa&ccedil;&atilde;o.</p>\r\n','<p>CORRETA<br />\r\nO tema do texto &eacute; a seguran&ccedil;a digital. Por meio de apresenta&ccedil;&atilde;o de informa&ccedil;&otilde;es e de opini&otilde;es de especialistas nessa &aacute;rea, o texto esbo&ccedil;a a tese de que seria necess&aacute;rio o Brasil construir um aparato de seguran&ccedil;a visando &agrave; prote&ccedil;&atilde;o das empresas e das tecnologias desenvolvidas no pa&iacute;s.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62296,12718,'C','<p>discutir a espionagem em f&oacute;runs internacionais.</p>\r\n','<p>INCORRETA<br />\r\nO texto at&eacute; cita a espionagem corporativa (&ldquo;Nessa hist&oacute;ria de espionagem corporativa, temos muita li&ccedil;&atilde;o a fazer&rdquo;), o que pode incentivar a marca&ccedil;&atilde;o dessa alternativa, mas n&atilde;o h&aacute; nenhuma refer&ecirc;ncia a f&oacute;runs internacionais.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62297,12718,'D','<p>incentivar o aparecimento de delatores.</p>\r\n','<p>INCORRETA<br />\r\nO aparecimento de delatores &eacute; citado brevemente no texto (&ldquo;estabelecidos canais de den&uacute;ncia para esse tipo de situa&ccedil;&atilde;o&rdquo;), mas n&atilde;o corresponde com a maior necessidade advinda do crescimento da Internet. Isso torna a alternativa incorreta. &Eacute; poss&iacute;vel tamb&eacute;m que essa alternativa seja marcada caso se fa&ccedil;a a associa&ccedil;&atilde;o entre o conhecimento pr&eacute;vio e externo ao texto sobre o jornalista Julian Assange (fundador do site WikiLeaks, que delatou, por meio de sua plataforma, informa&ccedil;&otilde;es confidenciais e pol&ecirc;micas de empresas privadas e governos).</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62298,12718,'E','<p>treinar o pa&iacute;s em seguran&ccedil;a digital.</p>\r\n','<p>INCORRETA<br />\r\nO texto cita a necessidade de o pa&iacute;s estar mais bem preparado para amea&ccedil;as &agrave; seguran&ccedil;a da informa&ccedil;&atilde;o, o que poderia levar &agrave; marca&ccedil;&atilde;o dessa alternativa. Entretanto, o texto explicita que isso precisa ser feito por meio do fomento a &ldquo;empresas, a produ&ccedil;&atilde;o de conhecimento na &aacute;rea e a constru&ccedil;&atilde;o de uma cadeia de produ&ccedil;&atilde;o nacional&rdquo;, e n&atilde;o por meio de treinamento com os meios de prote&ccedil;&atilde;o digital que j&aacute; temos.</p>\r\n',0);
