INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62314,12722,'A','<p>&iacute;ndice de baixa escolaridade do falante.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O respondente deve ler atentamente o enunciado da quest&atilde;o e observar que j&aacute; foi avisado o fato de que o texto apresentado &eacute; uma &ldquo;transcri&ccedil;&atilde;o de fala&rdquo;, portanto, cont&eacute;m caracter&iacute;sticas pr&oacute;prias da linguagem oral, como as pausas recorrentes (representadas pelas retic&ecirc;ncias) e recursos lingu&iacute;sticos utilizados para manuten&ccedil;&atilde;o da conversa, como o &ldquo;n&eacute;&rdquo;, repetidos em todo o texto. Interpretar a presen&ccedil;a do &ldquo;n&eacute;&quot; como &iacute;ndice de baixa escolaridade do falante demonstra que o aluno n&atilde;o utilizou bem as informa&ccedil;&otilde;es dadas pelo enunciado.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62315,12722,'B','<p>estrat&eacute;gia t&iacute;pica da manuten&ccedil;&atilde;o da intera&ccedil;&atilde;o&nbsp;oral.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>O texto foi extra&iacute;do de um estudo lingu&iacute;stico sobre as propriedades da l&iacute;ngua falada na regi&atilde;o de Natal. Em seu original, o texto foi transcrito de uma conversa ou entrevista entre o autor e o depoente &ndash; a pessoa que contou a hist&oacute;ria. Comum na linguagem falada e aceito nos di&aacute;logos, &ldquo;n&eacute;&rdquo; &eacute; um termo cujo emprego volta-se inteiramente para &ldquo;a manuten&ccedil;&atilde;o da intera&ccedil;&atilde;o oral&rdquo;, ou seja, para &ldquo;testar&rdquo; a rela&ccedil;&atilde;o do falante com seu interlocutor, para garantir que o ouvinte estava prestando aten&ccedil;&atilde;o ao seu relato. Trata-se do emprego da fun&ccedil;&atilde;o f&aacute;tica da linguagem.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62316,12722,'C','<p>marca de&nbsp;conex&atilde;o l&oacute;gica entre&nbsp;conte&uacute;dos na fala.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>N&atilde;o &eacute; poss&iacute;vel interpretar o &ldquo;n&eacute;&quot; como marca de conex&atilde;o l&oacute;gica entre conte&uacute;dos na fala, pois sua fun&ccedil;&atilde;o &eacute; somente f&aacute;tica, de manter a aten&ccedil;&atilde;o oral de seu ouvinte, ou seja, &eacute; uma das express&otilde;es conectoras que n&atilde;o modificam a estrutura do discurso, mas servem para chamar a aten&ccedil;&atilde;o do receptor da mensagem.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62317,12722,'D','<p>manifesta&ccedil;&atilde;o caracter&iacute;stica da&nbsp;fala nordestina.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Apesar de realmente tamb&eacute;m estar presente na fala nordestina, o que pode confundir o respondente desatento, o &ldquo;n&eacute;&quot; n&atilde;o &eacute; restrito a ela, pois n&atilde;o se trata de uma varia&ccedil;&atilde;o regional, mas de um recurso da linguagem oral comum aos falantes em todo o Brasil.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62318,12722,'E','<p>recurso enfatizador da&nbsp;informa&ccedil;&atilde;o mais relevante da&nbsp;narrativa.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O termo &ldquo;n&eacute;&quot; no texto n&atilde;o exerce a fun&ccedil;&atilde;o de enfatizador de informa&ccedil;&otilde;es, pois &eacute; utilizado como elemento de manuten&ccedil;&atilde;o da intera&ccedil;&atilde;o entre emissor e receptor da mensagem.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62319,12723,'A','<p>A modifica&ccedil;&atilde;o das regras busca associar valores l&uacute;dicos ao MMA, possibilitando a participa&ccedil;&atilde;o de diferentes popula&ccedil;&otilde;es como atividade de lazer.</p>\r\n','',0);
