INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62321,12723,'C','<p>As mudan&ccedil;as de regras do MMA atendem &agrave; necessidade de tornar a modalidade menos violenta, visando sua introdu&ccedil;&atilde;o nas academias de gin&aacute;stica na dimens&atilde;o da sa&uacute;de.</p>\r\n','',0);
