INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62324,12724,'A','<p>&eacute; indispens&aacute;vel para as pessoas que fazem atividades f&iacute;sicas regularmente.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O texto informa que o uso de suplementos &eacute; indicado a um grupo espec&iacute;fico de pessoas, dentro do qual pode se encontrar atletas de alto rendimento que n&atilde;o possuam alimenta&ccedil;&atilde;o balanceada. Portanto, n&atilde;o se trata de qualquer pessoa que fa&ccedil;a atividades f&iacute;sicas regularmente.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62325,12724,'B','<p>&eacute; estimulada pela ind&uacute;stria voltada para adolescentes que buscam um corpo ideal.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>Segundo o texto, a ingest&atilde;o de suplementos &eacute; impulsionada pelas propagandas de ind&uacute;strias do ramo, que afetam especialmente aos adolescentes por eles estarem em um momento de autoafirma&ccedil;&atilde;o.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62326,12724,'C','<p>&eacute; indicada para atividades f&iacute;sicas como a muscula&ccedil;&atilde;o com fins de promo&ccedil;&atilde;o da sa&uacute;de.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A indica&ccedil;&atilde;o de suplementos &eacute; feita apenas para um grupo espec&iacute;fico de pessoas, conforme o texto. Desse modo, n&atilde;o se prop&otilde;e que esses produtos sejam recomendados a pessoas que fazem atividades f&iacute;sicas com o objetivo de ter uma boa sa&uacute;de.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62327,12724,'D','<p>direciona-se para adolescentes com dist&uacute;rbios metab&oacute;licos e que praticam atividades f&iacute;sicas.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A refer&ecirc;ncia feita aos adolescentes no texto envolve a manipula&ccedil;&atilde;o praticada com eles pela ind&uacute;stria de suplementos, no intuito de seduzi-los como consumidores. N&atilde;o h&aacute;, portanto, refer&ecirc;ncia a dist&uacute;rbios metab&oacute;licos como o perfil para a ingest&atilde;o de suplementos.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62328,12724,'E','<p>melhora&nbsp;a sa&uacute;de do indiv&iacute;duo que n&atilde;o tem uma dieta balanceada e nem pratica atividades f&iacute;sicas.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Menciona-se que suplementos podem ajudar atletas de alto rendimento que n&atilde;o t&ecirc;m dieta balanceada. N&atilde;o h&aacute; refer&ecirc;ncia a indiv&iacute;duos que n&atilde;o praticam atividades f&iacute;sicas como aqueles que precisariam ingerir o produto.</p>\r\n',0);
