INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62359,12731,'A','<p>uma refer&ecirc;ncia ao contexto, &quot;tr&acirc;nsito no feriad&atilde;o&quot;, esclarecendo-se o referente tanto ao texto de Iotti quanto da obra de Picasso.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O respondente demonstrar&aacute; falta de conhecimento da obra de Picasso ao marcar essa alternativa, pois o &ldquo;tr&acirc;nsito no feriad&atilde;o&rdquo; n&atilde;o faz parte dela. O carro foi o elemento introduzido por Iotti. Al&eacute;m disso, mesmo sem conhecer a obra <em>Guernica</em>, ao interpretar o texto do enunciado, o aluno deveria utilizar melhor as informa&ccedil;&otilde;es para perceber que, em um painel&nbsp;sobre a guerra, este n&atilde;o seria o elemento relevante.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62360,12731,'B','<p>uma refer&ecirc;ncia ao tempo presente, com o emprego da forma verbal &quot;&eacute;&quot;, evidenciando-se atualidade do tema abordado tanto pelo pintor espanhol quanto pelo chargista brasileiro.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O emprego da forma verbal &quot;&eacute;&quot; n&atilde;o &eacute; o elemento principal de estabelecimento de di&aacute;logo entre as obras. Ele poderia ser substitu&iacute;do por outro e mesmo assim o di&aacute;logo aconteceria.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62361,12731,'C','<p>um termo pejorativo, &quot;tr&acirc;nsito&quot;, refor&ccedil;ando-se a imagem negativa de mundo ca&oacute;tico presente, tanto em Guernica quanto na charge.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O aluno que marcar tal alternativa demonstrar&aacute; interpreta&ccedil;&atilde;o subjetiva do texto para julgar que &quot;tr&acirc;nsito&quot; &eacute; um termo pejorativo que refor&ccedil;a a imagem negativa de mundo ca&oacute;tico. H&aacute; outros elementos verbais que realizam o di&aacute;logo entre as obras como foco.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62362,12731,'D','<p>uma refer&ecirc;ncia temporal, &quot;sempre&quot;, referindo-se &agrave; perman&ecirc;ncia de trag&eacute;dias retratadas tanto em Guernica, quanto na charge.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A refer&ecirc;ncia temporal &quot;sempre&quot; est&aacute; circunscrita &agrave; situa&ccedil;&atilde;o &ldquo;tr&acirc;nsito no feriad&atilde;o&rdquo; e n&atilde;o faz di&aacute;logo com a obra de Picasso. Julgar tal alternativa correta demonstrar&aacute; que o respondente n&atilde;o conseguiu interpretar corretamente a fala contida na charge, tampouco associ&aacute;-la &agrave; obra.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62363,12731,'E','<p>uma express&atilde;o poliss&ecirc;mica, &quot;quadro dram&aacute;tico&quot;, remetendo-se tanto &agrave; obra pict&oacute;rica quanto ao contexto de transito brasileiro.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>Ao analisar a linguagem verbal usada na charge, h&aacute; v&aacute;rios significados na express&atilde;o &ldquo;quadro dram&aacute;tico&rdquo;, ou seja, h&aacute; polissemia. Os significados se referem aos horrores do massacre de Guernica, retratados no painel de Picasso, e ao tr&acirc;nsito ca&oacute;tico nos feriados, assim, contribui-se para estabelecer um di&aacute;logo entre as obras.</p>\r\n',1);
