INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62415,12742,'A','<p>filos&oacute;fico, pois reflete sobre as mazelas sofridas pelos vizinhos.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A cr&ocirc;nica n&atilde;o possui teor reflexivo, n&atilde;o fazendo problematiza&ccedil;&atilde;o ou questionamento, portanto, o car&aacute;ter n&atilde;o pode ser filos&oacute;fico. O aluno pode escolher esse distrator caso o texto cause uma reflex&atilde;o pessoal.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62416,12742,'B','<p>l&iacute;rico, pois relata com nostalgia o relacionamento da vizinhan&ccedil;a.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>N&atilde;o h&aacute; na cr&ocirc;nica aspectos l&iacute;ricos ou autorreflexivos. Al&eacute;m disso, a retrata&ccedil;&atilde;o da conviv&ecirc;ncia entre os vizinhos n&atilde;o &eacute; nost&aacute;lgica, n&atilde;o refletindo saudosismo. O aluno s&oacute; escolher&aacute; esse distrator caso sua leitura n&atilde;o tenha sido minuciosa.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62417,12742,'C','<p>ir&ocirc;nico, pois apresenta com mal&iacute;cia a conviv&ecirc;ncia entre vizinhos.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>O teor ir&ocirc;nico faz-se presente, pois retrata de maneira maliciosa a conviv&ecirc;ncia entre os vizinhos (&ldquo;Como &eacute; o neg&oacute;cio?&rdquo;), marcada, no texto, pelo adult&eacute;rio e pela libertinagem.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62418,12742,'D','<p>cr&iacute;tico, pois deprecia o que acontece nas rela&ccedil;&otilde;es de vizinhan&ccedil;a.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O texto n&atilde;o faz julgamento de valor. Embora seja cr&iacute;tico, n&atilde;o h&aacute; deprecia&ccedil;&atilde;o, portanto, apenas uma parte do distrator &eacute; relevante. O aluno pode escolher esse distrator caso considere apenas essa parte correta.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62419,12742,'E','<p>did&aacute;tico, pois exp&otilde;e uma conduta a ser evitada na rela&ccedil;&atilde;o entre vizinhos.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O didatismo envolve o aprendizado e o ensino, e nenhum dos dois est&aacute; inserido no texto. Al&eacute;m disso, como n&atilde;o h&aacute; ju&iacute;zo de valor, n&atilde;o &eacute; poss&iacute;vel afirmar que o texto sugere que essa conduta seja evitada. O distrator falha, portanto, como resposta. O aluno pode marcar esse distrator caso enxergue algum julgamento de valor no conto ou caso n&atilde;o compreenda o significado da palavra &ldquo;did&aacute;tico&rdquo;.</p>\r\n',0);
