INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62495,12758,'A','<p>&ldquo;nisso&rdquo; introduz o fragmento &ldquo;botar a cara na janela em cr&ocirc;nica de jornal&rdquo;.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>O pronome demonstrativo isso, em nisso, refere-se ao trecho imediatamente posterior: &ldquo;botar a cara na janela em cr&ocirc;nica de jornal&rdquo;. &Eacute; preciso que o respondente consiga compreender as refer&ecirc;ncias feitas, para demonstrar o dom&iacute;nio de recursos coesivos.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62496,12758,'B','<p>&nbsp;&ldquo;assim &rdquo;&eacute; uma par&aacute;frase de &ldquo;&eacute; como me botarem no colo&rdquo;.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A express&atilde;o &ldquo;&eacute; como me botarem no colo&rdquo; refere-se aos est&iacute;mulos j&aacute; citados pela autora na frase, portanto, n&atilde;o se relaciona com o termo &ldquo;assim&rdquo; com o sentido enunciado pela alternativa.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62497,12758,'C','<p>&ldquo;isso&rdquo; remete a &ldquo;escondia em poesia e fic&ccedil;&atilde;o&rdquo;.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Ao interpretar essa alternativa como correta, o respondente demonstra dificuldade em compreender os recursos de coes&atilde;o do texto. O pronome isso se refere ao trecho imediatamente anterior: &ldquo;botar a cara na janela em cr&ocirc;nica de jornal&rdquo;.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62498,12758,'D','<p>&ldquo;alguns&quot;&nbsp;antecipa a informa&ccedil;&atilde;o &ldquo;&Eacute; isso que digo para meus pais&rdquo;.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A afirma&ccedil;&atilde;o desta alternativa est&aacute; errada, pois o pronome &ldquo;Alguns&rdquo; tem a fun&ccedil;&atilde;o de remeter a &ldquo;escritores&rdquo;, visto que, no trecho em que est&aacute; inserida, a palavra enuncia que &ldquo;alguns &lsquo;escritores&rsquo; discorrem sobre o assunto&rdquo;.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (62499,12758,'E','<p>&ldquo;essa&rdquo; recupera a informa&ccedil;&atilde;o anterior &ldquo;janela do jornal&rdquo;.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Apesar de o pronome &ldquo;essa&rdquo; poder retomar o que j&aacute; foi dito no texto em que estiver inserida, no trecho em destaque, ela n&atilde;o recupera a informa&ccedil;&atilde;o &ldquo;janela do jornal&rdquo;, pois, retoma o que est&aacute; dito imediatamente antes dela: &ldquo;Porque eu levo a s&eacute;rio ser s&eacute;rio... mesmo quando parece que estou brincando: essa &eacute; uma das maravilhas de escrever&rdquo;.</p>\r\n',0);
