INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (101766,21307,'A','<p>&nbsp;oscila&ccedil;&atilde;o gen&eacute;tica das ra&ccedil;as.</p>\r\n','<p>INCORRETA.</p>\r\n\r\n<p>N&atilde;o compreende que a deriva ou oscila&ccedil;&atilde;o gen&eacute;tica &eacute; a mudan&ccedil;a, ao acaso, das frequ&ecirc;ncias al&eacute;licas. Tal fen&ocirc;meno &eacute; caracterizado pela ocorr&ecirc;ncia de cat&aacute;strofes ecol&oacute;gicas limitando, dessa forma, o teor gen&eacute;tico de um determinado grupo, restrito aos indiv&iacute;duos prevalecentes. Isso n&atilde;o explica o fato da dificuldade reprodutiva entre ra&ccedil;as de c&atilde;es dom&eacute;sticos com grande diferen&ccedil;a em seus tamanhos corporais n&atilde;o ocasionar o processo de especia&ccedil;&atilde;o.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (101767,21307,'B','<p>&nbsp;converg&ecirc;ncia adaptativa das ra&ccedil;as.</p>\r\n','<p>INCORRETA.</p>\r\n\r\n<p>N&atilde;o compreende que a converg&ecirc;ncia adaptativa &eacute; um processo em que organismos de diferentes esp&eacute;cies, mas que vivem em um mesmo tipo de ambiente por muito tempo, acabam adquirindo semelhan&ccedil;as morfol&oacute;gicas pelo processo evolutivo, gra&ccedil;as &agrave; sele&ccedil;&atilde;o natural.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (101768,21307,'C','<p>&nbsp;isolamento geogr&aacute;fico entre as ra&ccedil;as.</p>\r\n','<p>INCORRETA.</p>\r\n\r\n<p>N&atilde;o compreende que o isolamento geogr&aacute;fico pode ocasionar o processo de especia&ccedil;&atilde;o.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (101769,21307,'D','<p>&nbsp;sele&ccedil;&atilde;o natural que ocorre entre as ra&ccedil;as.</p>\r\n','<p>INCORRETA.</p>\r\n\r\n<p>N&atilde;o compreende que a sele&ccedil;&atilde;o natural atua sobre todas as esp&eacute;cies, o que n&atilde;o explica o fato da dificuldade reprodutiva entre ra&ccedil;as de c&atilde;es dom&eacute;sticos com grande diferen&ccedil;a em seus tamanhos corporais n&atilde;o ocasionar o processo de especia&ccedil;&atilde;o.</p>\r\n',0);
